use class10;
CREATE TABLE Orders (    OrderID INT PRIMARY KEY,    CustomerName VARCHAR(100),    OrderDate DATE);CREATE TABLE OrderDetails (    OrderDetailID INT PRIMARY KEY,    OrderID INT,    ProductName VARCHAR(100),    Quantity INT,    UnitPrice DECIMAL(10,2),    FOREIGN KEY (OrderID) REFERENCES Orders(OrderID));INSERT INTO Orders VALUES     (1, 'Alice', '2024-03-01'),    (2, 'Bob', '2024-03-02'),    (3, 'Charlie', '2024-03-03');INSERT INTO OrderDetails VALUES     (1, 1, 'Laptop', 1, 1000.00),    (2, 1, 'Mouse', 2, 50.00),    (3, 2, 'Phone', 1, 700.00),    (4, 2, 'Charger', 1, 30.00),    (5, 3, 'Tablet', 1, 400.00),    (6, 3, 'Keyboard', 1, 80.00);select * from orders;select * from orderdetails;select o.customername, o.orderid, od.price from orders o join (select orderid, max(unitprice) as price from orderdetails group by orderid) od on o.orderid=od.orderid;;with od as (select orderid, max(unitprice) as price from orderdetails group by orderid)select o.customername, o.orderid, od.price from orders o join od on o.orderid=od.orderid; select o1.*, od.productname from orderdetails od join (select orderid, customername, (select top 1 unitprice  from orderdetails od  where od.orderid=o.orderid order by unitprice desc) as mxfrom orders as o) o1 on o1.mx=od.unitprice;select o1.*, od.productname from orderdetails od join (select orderid, customername, (select max(unitprice)  from orderdetails od  where od.orderid=o.orderid ) as mxfrom orders as o) o1 on o1.mx=od.unitprice;CREATE TABLE TestMax(    Year1 INT    ,Max1 INT    ,Max2 INT    ,Max3 INT);INSERT INTO TestMax VALUES    (2001,10,101,87)    ,(2002,103,19,88)    ,(2003,21,23,89)    ,(2004,27,28,91);select * from testmax;select year1, greatest(max1, max2, max3) from testmax;select year1, case when max1>max2 and max1>max3 then max1     when max2>max3 then max2 else max3 end as mx from testmax;--correlationselect * from (  values    (1,2),	(3,4)) t(col1, col2);select * from (select 1 as col1,2 as col2union allselect 3, 4) t;select max(val) from(  values (1), (2), (3) ) t(val);select year1, (select max(val) from ( values (max1), (max2), (max3)) t(val)) from testmax;